          <div className="mb-8">
            <Link href="/">
              <span className="inline-flex items-center gap-2 text-sm text-muted-foreground hover:text-foreground mb-4 cursor-pointer" data-testid="link-back-home">
                <ArrowLeft className="w-4 h-4" />
